mapper.select:
SELECT grp_id, grp_nm, dc, oprtr_yn FROM tb_code_grp WHERE 1 = 1
AND grp_nm LIKE '%' || #{grpNm} || '%'
AND oprtr_yn = #{oprtrYn}

SELECT COUNT(*)
FROM   tb_code
WHERE  grp_id  = #{grpId}
AND    code_nm = #{codeNm}

mapper.update:
UPDATE tb_code
SET    code_nm = #{codeNm},
       yn      = #{yn},
       updt_dt = SYSDATE
WHERE  grp_id  = #{grpId}
AND    code_id = #{codeId}
