mapper.select:
    SELECT
      /* 전체 사원 수 */
      (SELECT COUNT(*)
         FROM tb_emp e
        WHERE e.vend_id = #{vendId}
      ) AS totalEmp,

      /* 계정 미부여 사원 수 (사원은 있으나 tb_emp_acct 없음) */
	  (SELECT COUNT(*)
	     FROM tb_emp e
	     LEFT JOIN tb_emp_acct a
	       ON a.vend_id = e.vend_id
	      AND a.emp_id  = e.emp_id 
	    WHERE e.vend_id = #{vendId}
	      AND a.emp_id IS NULL
	  ) AS noAcctEmp,

      /* 권한 없는 계정 수(역할 미지정) */
      (SELECT COUNT(*)
         FROM tb_emp_acct a
         LEFT JOIN tb_user_role ur
           ON ur.emp_acct_id = a.emp_acct_id
          AND ur.vend_id     = a.vend_id
        WHERE a.vend_id = #{vendId}
          AND ur.emp_acct_id IS NULL
      ) AS noRoleAcct,

      /* 오늘 로그인 성공(lg1) */
      (SELECT COUNT(*)
         FROM tb_log l
        WHERE l.vend_id = #{vendId}
          AND l.err_dttm >= TRUNC(SYSDATE)
          AND l.motion_ty = 'lg1'
      ) AS loginSuccToday,

      /* 오늘 로그인 실패(lg2) */
      (SELECT COUNT(*)
         FROM tb_log l
        WHERE l.vend_id = #{vendId}
          AND l.err_dttm >= TRUNC(SYSDATE)
          AND l.motion_ty = 'lg2'
      ) AS loginFailToday,

      /* 오늘 OTP 실패(lg5) */
      (SELECT COUNT(*)
         FROM tb_log l
        WHERE l.vend_id = #{vendId}
          AND l.err_dttm >= TRUNC(SYSDATE)
          AND l.motion_ty = 'lg5'
      ) AS otpFailToday,

      /* 오늘 잠금 발생(lg3) */
      (SELECT COUNT(*)
         FROM tb_log l
        WHERE l.vend_id = #{vendId}
          AND l.err_dttm >= TRUNC(SYSDATE)
          AND l.motion_ty = 'lg3'
      ) AS lockedToday,

      /* 현재 잠김 계정 수 (tb_emp_acct.st = 'r2') */
      (SELECT COUNT(*)
         FROM tb_emp_acct a
        WHERE a.vend_id = #{vendId}
          AND a.st = 'r2'
      ) AS lockedNow

    FROM dual
